sys_script_client.script:
function onSubmit() {

	var ans = "";
	if(g_form.getValue('impact') == 1 && g_form.getValue('urgency') == 1 && !g_user.hasRoleExactly('x_58872_needit.major_inc_mgr')){
		ans = confirm( g_user.firstName + "The customer is notified of all Priority -1 incidents. Confirm base information is included before submitting this P1. \n\nselect ok to submit or cancel to return to the record.");

		if(!ans){
			g_form.addInfoMessage("Incident not submitted.");
			g_form.addInfoMessage("If base information is unabailable, use the 'Additional comments' field to document why it is missing.");
			g_form.showFieldMsg('category', "Major incident base field");
			g_form.showFieldMsg('cmdb_ci', "Major Incident base field");
			g_form.showFieldMsg('assignment_group', "Major Incident base field");
			g_form.showFieldMsg('short_decription',"Major Incident base field");
		}
	return ans;	
	}



}
